import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [username, setUsername] = useState("");
  const [error, setError] = useState("");
  const [isShaking, setIsShaking] = useState(false); // state for shake effect
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const isValidNumber = /^\d{10}$/.test(username);
    if (!isValidNumber) {
      setError("Use your Student ID please");
      setIsShaking(true); // set isShaking to true to trigger the shake effect
      return;
    }
    localStorage.setItem("_username", username);
    navigate("/app");
  };

  const handleInputChange = (e) => {
    setUsername(e.target.value);
    setIsShaking(false); // reset isShaking to false when input changes
  };

  return (
    <div className="home" style={{ background: "lightgray" }}>
      <h2>Sign in to your Homework</h2>
      <form onSubmit={handleSubmit} className="home__form">
        <label htmlFor="username">Your Student Number</label>
        <input
          value={username}
          required
          onChange={handleInputChange}
          className={`input ${isShaking ? "shake" : ""}`} // add shake class dynamically
          maxLength={10}
          type="number"
        />
        <button>SIGN IN</button>
        {error && <div className="error">{error}</div>}
      </form>
    </div>
  );
};

export default Home;
