import React, { useEffect, useRef, useState } from "react";

const Modal = ({ showModal, setShowModal, selectedItemID, socket }) => {
  const modalRef = useRef();
  const [comment, setComment] = useState("");
  const [comments, setComments] = useState([]);

  const closeModal = (e) => {
    if (modalRef.current === e.target) {
      setShowModal(!showModal);
    }
  };
  const addComment = (e) => {
    e.preventDefault();
    socket.emit("updateComment", {
      todoID: selectedItemID,
      comment,
      user: localStorage.getItem("_username"),
    });
    setComment("");
  };
  useEffect(() => {
    socket.on("commentsReceived", (todo) => setComments(todo.comments));
  }, [socket]);

  return (
    <div
      className="modal"
      onClick={closeModal}
      ref={modalRef}
      style={{
        backgroundImage: `url('https://cdn2.vectorstock.com/i/1000x1000/32/26/light-white-gray-texture-background-digital-vector-28503226.jpg')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
      }}
    >
      <div className="modal__container">
        <h3>Comments</h3>
        <form className="comment__form" onSubmit={addComment}>
          <input
            className="comment__input"
            type="text"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            required
          />
          <button>Add Comment</button>
          <button className="returnBtn" onClick={() => setShowModal(false)}>
            RETURN TO MAIN PAGE
          </button>
        </form>
        <div className="comments__container">
          {comments.length > 0 ? (
            comments.map((item, index) => (
              <div className="comment" key={index}>
                <p>
                  <strong>{item.name} - </strong> {item.text}
                </p>
              </div>
            ))
          ) : (
            <p>No comments available yet...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Modal;
